import React, { useState, useEffect } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import axios from 'axios';
    




export default function Update() {
    const [Title, setTitle] = useState('');
    const [Name, setName] = useState('');
    const [Content, setContent] = useState(" ");
    const [id, setID] = useState(null);

    useEffect(() => {
        setID(localStorage.getItem('ID'))
        setTitle(localStorage.getItem('First Name'));
        setName(localStorage.getItem('Last Name'));
        setContent(localStorage.getItem('Checkbox Value'))
}, []);

const updateAPIData = () => {
    console.log("hello")
    axios.put(`http://localhost:3000/genuine/editPost/${id}`, {
        title,
         Name,
         Content,
	})
}
    return (
        <div>
            <Form className="create-form">
                <Form.Field>
                    <label>First Name</label>
                    <input placeholder='First Name' value={Title} onChange={(e) => setTitle(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Last Name</label>
                    <input placeholder='Last Name' value={Name} onChange={(e) => setName(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                <label>Content</label>
                    <input label='Content' value={Content}  onChange={(e) => setContent(e.target.value)}/>
                </Form.Field>
                <Button onClick={updateAPIData} type='submit'>Update</Button>
            </Form>
        </div>
    )
}
