import axios from 'axios'
import React, { Component } from 'react'

 class PostForm extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         title:'',
         author: '',
         content: ''
      }
    }
    changeHandler = e=>{
        console.log("hello")
        this.setState({[e.target.name]:e.target.value})
    }
    submitHandler= e=>{
        e.preventDefault()
        console.log(this.state)
        axios.post('http://localhost:3000/genuine/createPost', this.state)
        .then(response=>{
            console.log(response);
        })
        .catch(error=>{
            console.log(error)
        })

        var a=document.querySelector('#container');
        
    }
  render() {
    const {title,author,content}=this.state;
    return (
    
      <div>
        <h1>Post Your blog </h1>
        <form className='myForm' onSubmit= {this.submitHandler}>
            <div>
              <label>
                  Title
              </label>
              <br></br>
                <input type="text" 
                name="title" 
                defaultValue={title} 
                onChange={this.changeHandler} />
            </div>
            <div>

            <label>
                  Author
              </label>
              <br></br>
                <input type="text" 
                name="author" 
                defaultValue={author}
                onChange={this.changeHandler} />
            </div>
            <div>
            <label>
                    Content
              </label>
              <br></br>
              
                <textarea name="content" 
                defaultValue={content}
                onChange={this.changeHandler} 
                rows="50"
                >
                
                </textarea>


                {/* <input type="text" 
                  
                name="content" 
                defaultValue={content}
                onChange={this.changeHandler}  />
                 */}
            </div>
            <button type="submit" >Submit</button>
        </form>

      </div>
      
    )
  }
}

export default PostForm
