import React, { Component } from 'react'
import { Link } from 'react-router-dom'

import axios from 'axios';
 class PostsList extends Component {





    constructor(props) {
      super(props)
    
      this.state = {
         
         posts:[],
         unique:[]
        
      };
     

    }
    
    

    
    componentDidMount(){
        axios.get('http://localhost:3000/genuine/getAllPost',) // double 
        .then(response=> {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch( error => {
            console.log(error)
            this.setState({errorMsg: 'Error retrieving data'})
        })
        
    }

    show(id){
      console.log(id)
      axios.get(`http://localhost:3000/genuine/getPost/${id}`,)
      .then(response=>{
        console.log(response)
        this.setState({unique: response.data})
        alert(` title: ${response.data.title}  \n Author: ${response.data.author} \n Content: ${response.data.content}`)
      })
      .catch(error=>{
        console.log(error)
        this.setState({errorMsg:"Error receiving unique data"})
      })



   }


   delete(id){
    axios.delete(`http://localhost:3000/genuine/deletePost/${id}`,)






      // .then(response=>{
      //   console.log(response)
        
        
      // })
     

      // .then(res=>res.json())
      // .then(res2=>{
      //   const up=this.state.posts.filter(item=>{
      //     return item._id !==res2._id
      //   })
      // })
      
      // .catch(error=>{
      //   console.log(error)
      //   this.setState({errorMsg:"Error in Deletion"})
      // },)

    
   }

   
  render() {


    const {posts,errorMsg}=this.state;


   
    
    return (
      <table>PostsList
        <tbody>
        <tr>
            <th>Title</th>
            <th>Author</th>
            <th> Content</th>
            <th> id</th>
        </tr>
      
        
        {
            posts.length ?
            posts.map(post => 
            <tr key= {posts.title}>
              <td>{ post.author} </td> 
              <td>{post.title} </td>
              <td>{post.content}</td>
              <td>{post._id}</td>
              
        <td>   
        
          
          </td>


          <div>
          <button onClick={()=>this.show(post._id)}> Show unique</button>
          <button onClick={()=>this.delete(post._id)}>Delete Post</button>
        </div>


         

                















              
     
            </tr>
                 
              
            ): null
        }   
        { errorMsg ? <div> {errorMsg} </div> :null}


       
        </tbody>
        <div id="sarthak"></div>
      </table>
      
    )
  }
}
 

export default PostsList
