import React, { useState, useEffect } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react'
import axios from 'axios';
    




export default function Update() {
    const [Title, setTitle] = useState('');
    const [Name, setName] = useState('');
    const [Content, setContent] = useState("");
    const [id, setID] = useState("");
   
    useEffect(() => {
        setID(localStorage.getItem('ID'))
        
        setTitle(localStorage.getItem('title'));
        setName(localStorage.getItem('Name'));
        setContent(localStorage.getItem('Content'))
}, []);

const updateAPIData = () => {
    console.log("hello")
    console.log(Title)
    axios.patch(`http://localhost:3000/genuine/editPost/${id}`,
            { title:Title, author:Name, content:Content  },
           
        ).then((response) => {
            console.log(response)
        }).catch((error) => {
            console.log(error)
        })
;    
    // axios.put(`http://localhost:3000/genuine/editPost/${id}`, {
    //     Title,
        
    //      Name,
    //      Content,
         
	// })
    
}
    return (
        <div>
            <Form className="create-form">
                <Form.Field>
                    <label>Title</label>
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input placeholder='title' value={Title} onChange={(e) => setTitle(e.target.value)}/>
                </Form.Field>
                <Form.Field>
                    <label>Author</label>
                    &nbsp;&nbsp;<input placeholder='name' value={Name} onChange={(e) => setName(e.target.value)}/>
                </Form.Field>


                <Form.Field>
                <label>Content</label>
                <textarea label='Content' value={Content}  onChange={(e) => setContent(e.target.value)}></textarea>

                </Form.Field>



                <Button onClick={updateAPIData} type='submit'>Update this</Button>
            </Form>
        </div>
    )
}
