import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'
 class PostsList extends Component {

    constructor(props) {
      super(props)
    
      this.state = {
         
         posts:[],
         
      }
    }
    componentDidMount(){
        axios.get('http://localhost:3000/genuine/getAllPost',) // double 
        .then(response=> {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch( error => {
            console.log(error)
            this.setState({errorMsg: 'Error retrieving data'})
        })
        
    }


     viewlement=(e)=>{
      axios.get('http://localhost:3000/genuine/getPost',) // double 
        .then(response=> {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch( error => {
            console.log(error)
            this.setState({errorMsg: 'Error retrieving data'})
        })



    }
   
  render() {
    const {posts,errorMsg}=this.state;
    
    return (
      <table>PostsList
        <tr>
            <th>Title</th>
            <th>Author</th>
            <th> Content</th>
            <th> id</th>
        </tr>
        {
            posts.length ?
            posts.map(post => 
            <tr key= {posts.title}>
              <td>{ post.author} </td> 
              <td>{post.title} </td>
              <td>{post.content}</td>
              <td>{post._id}</td>
              
              <button  onclick={viewlement}> View</button>
              
            
            </tr>): null
        }   
        { errorMsg ? <div> {errorMsg} </div> :null}

      </table>
    )
  }
}

export default PostsList
