import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';
var Modal = require('react-bootstrap-modal')

 class PostsList extends Component {





    constructor(props) {
      super(props)
    
      this.state = {
         
         posts:[],
         unique:[]
        
      };
     

    }
    
    

    
    componentDidMount(){
        axios.get('http://localhost:3000/genuine/getAllPost',) // double 
        .then(response=> {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch( error => {
            console.log(error)
            this.setState({errorMsg: 'Error retrieving data'})
        })
        
    }

    show(id){
      console.log(id)
      axios.get(`http://localhost:3000/genuine/getPost/${id}`,)
      .then(response=>{
        console.log(response)
        this.setState({unique: response.data})
      })
      .catch(error=>{
        console.log(error)
        this.setState({errorMsg:"Error receiving unique data"})
      })

   }
   
   
  render() {
    let closeModal = () => this.setState({ open: false })
 
   

    const {posts,errorMsg}=this.state;


   
    
    return (
      <>
        <div>
        <button type='button'>Launch modal</button>
 
        <Modal
          show={this.state.open}
          onHide={closeModal}
          aria-labelledby="ModalHeader"
        >
          <Modal.Header closeButton>
            <Modal.Title id='ModalHeader'>A Title Goes here</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Some Content here</p>
          </Modal.Body>
          <Modal.Footer>
            // If you don't have anything fancy to do you can use
            // the convenient `Dismiss` component, it will
            // trigger `onHide` when clicked
            <Modal.Dismiss className='btn btn-default'>Cancel</Modal.Dismiss>
 
            // Or you can create your own dismiss buttons
            <button className='btn btn-primary' onClick={saveAndClose}>
              Save
            </button>
          </Modal.Footer>
        </Modal>
      </div>































      <table>PostsList
        <tbody>
        <tr>
            <th>Title</th>
            <th>Author</th>
            <th> Content</th>
            <th> id</th>
        </tr>
      
        
        {
            posts.length ?
            posts.map(post => 
            <tr key= {posts.title}>
              <td>{ post.author} </td> 
              <td>{post.title} </td>
              <td>{post.content}</td>
              <td>{post._id}</td>
              
        <td>   
        
          
          </td>


          <div>
          <button onClick={()=>this.show(post._id)}></button>
        </div>






              
     
            </tr>
                 
              
            ): null
        }   
        { errorMsg ? <div> {errorMsg} </div> :null}


       
        </tbody>
      </table>


          

      </>

    )
  }
}
 

export default PostsList
