  render() {


    const {posts,errorMsg}=this.state;


   
    
    return (
      <table>PostsList
        <tbody>
        <tr>
            <th>Title</th>
            <th>Author</th>
            <th> Content</th>
            <th> id</th>
        </tr>
      
        
        {
            posts.length ?
            posts.map(post => 
            <tr key= {posts.title}>
              <td>{ post.author} </td> 
              <td>{post.title} </td>
              <td>{post.content}</td>
              <td>{post._id}</td>
              
        <td>   
        
          
          </td>


          <div>
          <button onClick={()=>this.show(post._id)}> Show unique</button>
          <button onClick={()=>this.delete(post._id)}>Delete Post</button>
          <Link to="/update">
          <button onClick={()=>this.update(post)}>Edit and update</button>
          </Link>
        </div>


         

                















              
     
            </tr>
                 
              
            ): null
        }   
        { errorMsg ? <div> {errorMsg} </div> :null}


       
        </tbody>
        <div id="sarthak"></div>
      </table>
      
    )
  }
}
 

